import React from 'react';
import { Icon } from 'semantic-ui-react'
import './HamburgerMenuListItem.scss';
import { handleMouseEnter, handleMouseLeave } from '../../actions'
import { connect } from 'react-redux';

class HamburgerMenuListItem extends React.Component {

    render() {
        const hoverActiveClass = this.props.hamburgerMenuListItem.isHovered ? "hover-active" : "";
        return (
            <li onMouseEnter={() => this.props.handleMouseEnter()} onMouseLeave={() => this.props.handleMouseLeave()} className={" menu-list-items"+hoverActiveClass}>
                <Icon className={this.props.iconName} />{this.props.iconText}<Icon className="angle right " />
            </li>
        )
    }
};

const mapStateToProps = state => {
    return { hamburgerMenuListItem: state.hamburgerMenuListItem }
}

export default connect(mapStateToProps, { handleMouseEnter, handleMouseLeave })(HamburgerMenuListItem);
